import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GeocodingSearch from './components/GeocodingSearch';

function App() {
    const [selectedBusLine, setSelectedBusLine] = useState('');
    const [busData, setBusData] = useState([]);
    const [departureTime, setDepartureTime] = useState('');

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/linhas-onibus')
            .then(response => {
                const data = response.data;
                const select = document.getElementById('linhas-onibus');
                data.linhas.forEach(linha => {
                    const option = document.createElement('option');
                    option.text = linha;
                    select.add(option);
                });
            })
            .catch(error => {
                console.error('Erro ao buscar as linhas de ônibus:', error);
            });
    }, []);

    const handleBusLineSelection = async (busLine) => {
        setSelectedBusLine(busLine);
        try {
            const response = await axios.get(`http://127.0.0.1:8000/bus-data/${busLine}`);
            const currentTime = new Date(); // Obtemos o horário atual

            // Obtenha as coordenadas do ponto selecionado pelo usuário usando a função GeocodingSearch
            const pointCoordinates = await getPointCoordinates(); 

            const busDataWithTravelTime = await Promise.all(response.data.map(async (bus) => {
                // Converte as coordenadas do ônibus para o formato adequado
                const busCoordinates = { lat: parseFloat(bus.latitude.replace(',', '.')), lng: parseFloat(bus.longitude.replace(',', '.')) };

                // Calcula o tempo de viagem usando o Travel Time Matrix
                const travelTimeResponse = await axios.post('http://127.0.0.1:8000/calculate-travel-time', {
                    locations: [
                        { id: 'bus', coords: busCoordinates },
                        { id: 'point', coords: pointCoordinates },
                    ],
                    departure_searches: [
                        {
                            id: 'departure_search',
                            departure_location_id: 'bus',
                            arrival_location_ids: ['point'],
                            departure_time: currentTime.toISOString(),
                            travel_time: 1800, // Tempo de viagem esperado em segundos
                        },
                    ],
                });

                // Extrai o tempo de viagem do resultado
                const travelTime = travelTimeResponse.data.results[0].locations[0].properties[0].travel_time;

                return {
                    linha: bus.linha,
                    velocidade: bus.velocidade,
                    tempoPartida: Math.floor(travelTime / 60), // Convertendo de segundos para minutos
                };
            }));

            setBusData(busDataWithTravelTime || []);
        } catch (error) {
            console.error('Erro ao obter os dados dos ônibus da linha:', error);
        }
    };

    const getPointCoordinates = async () => {
        // Lógica para obter as coordenadas do ponto selecionado usando GeocodingSearch
        // Substitua a lógica abaixo pela chamada real à função GeocodingSearch
        return { lat: -22.9068, lng: -43.1729 };
    };

    const handlePointSelection = (result) => {
        console.log('Ponto selecionado:', result);
        // Adicione qualquer lógica adicional necessária aqui
    };

    const handleDepartureTimeSelection = (time) => {
        setDepartureTime(time);
    };

    useEffect(() => {
        const monitorBusPosition = async () => {
            const intervalId = setInterval(async () => {
                try {
                    const response = await axios.get(`http://127.0.0.1:8000/bus-position/${selectedBusLine}`);
                    const busPositions = response.data;

                    // Verificar se algum ônibus está a 10 minutos da localização selecionada dentro da janela de horário
                    const tenMinutesBeforeDeparture = new Date(departureTime);
                    tenMinutesBeforeDeparture.setMinutes(tenMinutesBeforeDeparture.getMinutes() - 10);

                    const busNearLocation = busPositions.find(bus => {
                        const busTimestamp = new Date(bus.timestamp);
                        return busTimestamp >= tenMinutesBeforeDeparture && busTimestamp <= new Date(departureTime);
                    });

                    if (busNearLocation) {
                        // Enviar e-mail de notificação para o usuário
                        console.log('Enviar e-mail de notificação para o usuário');
                    }
                } catch (error) {
                    console.error('Erro ao monitorar as posições dos ônibus:', error);
                }
            }, 60000); // Verificar a cada minuto

            return () => clearInterval(intervalId); // Limpar o intervalo quando o componente for desmontado
        };

        if (selectedBusLine && departureTime) {
            monitorBusPosition();
        }

    }, [selectedBusLine, departureTime]);

    return (
        <div>
            <GeocodingSearch onPointSelect={(result) => handlePointSelection(result)} />
            <select id="linhas-onibus" onChange={(e) => handleBusLineSelection(e.target.value)}>
                <option value="">Selecione uma linha de ônibus</option>
            </select>
            <input type="time" value={departureTime} onChange={(e) => handleDepartureTimeSelection(e.target.value)} />
            {selectedBusLine && (
                <div>
                    <h2>Ônibus da linha {selectedBusLine}</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Linha</th>
                                <th>Velocidade</th>
                                <th>Tempo até o local de partida</th>
                            </tr>
                        </thead>
                        <tbody>
                            {busData.map((bus, index) => (
                                <tr key={index}>    
                                    <td>{bus.linha}</td>
                                    <td>{bus.velocidade} km/h</td>
                                    <td>{bus.tempoPartida} min</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default App;
