import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [busesData, setBusesData] = useState([]);

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:8000/visualizar-onibus');
            setBusesData(response.data.onibus);
        } catch (error) {
            console.error('Erro ao buscar dados dos ônibus:', error);
        }
    };

    fetch('http://127.0.0.1:8000/linhas-onibus')
    .then(response => response.json())
    .then(data => {
        const select = document.getElementById('linhas-onibus');
        data.linhas.forEach(linha => {
            const option = document.createElement('option');
            option.text = linha;
            select.add(option);
        });
    });


    return (
        <div>
            <h1>Selecione um ônibus, ponto e horário de partida</h1>
            <button onClick={fetchData}>Buscar Ônibus</button>
            <h2>Tabela de Ônibus</h2>
            <ul>
                {busesData.map(bus => (
                    <li key={bus.id}>
                        {bus.linha} - {bus.velocidade} km/h - {bus.tempo} min até o ponto
                    </li>
                ))}
            </ul>
        </div>
    );

    <select id="linhas-onibus">
</select>

}

export default App;
