import React, { useState, useEffect } from 'react';
import axios from 'axios';
import GeocodingSearch from './components/GeocodingSearch';

function App() {
    const [selectedBusLine, setSelectedBusLine] = useState('');
    const [busData, setBusData] = useState([]);
    const [departureTime, setDepartureTime] = useState('');
    const [selectedPoint, setSelectedPoint] = useState(null);
    const [referenceTravelTime, setReferenceTravelTime] = useState(null);

    useEffect(() => {
        const referencePoint = { lat: -22.9519, lng: -43.2106 };
        const fetchData = async () => {
            try {
                const locations = [
                    { id: 'bus', coords: referencePoint },
                    // Adicione outras localizações de ônibus conforme necessário
                ];

                const departure_searches = [{
                    id: 'departure_search',
                    departure_location_id: 'bus',
                    arrival_location_ids: busData.map(bus => bus.ordem),
                    departure_time: departureTime,
                    travel_time: 1800, // Tempo de viagem esperado em segundos
                }];

                const response = await axios.post('http://127.0.0.1:8000/calculate-travel-time', { locations, departure_searches });

                const travelTimeResult = response.data.results.find(result => result.search_id === 'bus');
                setReferenceTravelTime(travelTimeResult ? Math.floor(travelTimeResult.locations[0].properties[0].travel_time / 60) : null);
            } catch (error) {
                console.error('Erro ao calcular o tempo de viagem até o ponto de referência:', error);
            }
        };

        if (departureTime) {
            fetchData();
        }
    }, [departureTime, busData]);

    // ...

const handleBusLineSelection = async (busLine) => {
    setSelectedBusLine(busLine);
    try {
        const response = await axios.get(`http://127.0.0.1:8000/bus-data/${busLine}`);
        setBusData(response.data.map(bus => ({ ...bus, tempoPartida: null })) || []);
    } catch (error) {
        console.error('Erro ao obter os dados dos ônibus da linha:', error);
    }
};

useEffect(() => {
    const fetchBusLines = async () => {
        try {
            const response = await axios.get('http://127.0.0.1:8000/linhas-onibus');
            const data = response.data;
            const select = document.getElementById('linhas-onibus');
            select.innerHTML = ''; // Limpa as opções anteriores

            if (data.linhas && data.linhas.length > 0) {
                data.linhas.forEach(linha => {
                    const option = document.createElement('option');
                    option.text = linha;
                    select.add(option);
                });
            } else {
                console.error('Erro: Nenhuma linha de ônibus encontrada.');
            }
        } catch (error) {
            console.error('Erro ao buscar as linhas de ônibus:', error);
        }
    };

    fetchBusLines();
}, []);  // Certifica-se de que a função seja executada apenas uma vez durante a montagem do componente

// ...


    const handlePointSelection = (result) => {
        console.log('Ponto selecionado:', result);
        setSelectedPoint(result);
    };

    const handleDepartureTimeSelection = (time) => {
        setDepartureTime(time);
    };

    useEffect(() => {
        const calculateTravelTime = async () => {
            if (selectedPoint) {
                try {
                    const locations = [
                        { id: 'bus', coords: { lat: parseFloat(selectedPoint.lat), lng: parseFloat(selectedPoint.lng) } },
                        // Adicione outras localizações de ônibus conforme necessário
                    ];

                    const departure_searches = [{
                        id: 'departure_search',
                        departure_location_id: 'bus',
                        arrival_location_ids: busData.map(bus => bus.ordem),
                        departure_time: departureTime,
                        travel_time: 1800, // Tempo de viagem esperado em segundos
                    }];

                    // Simulação de tempos de viagem aleatórios
                    const randomTravelTimes = {};
                    busData.forEach(bus => {
                        randomTravelTimes[bus.ordem] = Math.floor(Math.random() * 1800) + 300; // Números aleatórios entre 300 e 2100 segundos
                    });

                    // Simulação de resposta da API
                    const response = {
                        data: {
                            results: [
                                {
                                    search_id: 'bus',
                                    locations: locations.map(loc => ({
                                        id: loc.id,
                                        properties: [{ travel_time: randomTravelTimes[loc.id] }]
                                    }))
                                }
                            ]
                        }
                    };

                    const ordemToTravelTime = response.data.results[0].locations.reduce((acc, loc) => {
                        acc[loc.id] = loc.properties[0].travel_time;
                        return acc;
                    }, {});

                    const updatedBusData = busData.map(bus => {
                        const travelTime = ordemToTravelTime[bus.ordem];
                        return {
                            ...bus,
                            tempoPartida: travelTime !== undefined ? Math.floor(travelTime / 60) : null, // Dividindo por 60 para converter segundos para minutos
                        };
                    });

                    setBusData(updatedBusData);
                } catch (error) {
                    console.error('Erro ao calcular o tempo de viagem:', error);
                }
            }
        };

        if (selectedBusLine && departureTime && selectedPoint) {
            calculateTravelTime();
        }

    }, [selectedBusLine, departureTime, selectedPoint, busData]);

    return (
        <div>
            <GeocodingSearch onPointSelect={(result) => handlePointSelection(result)} />
            <select id="linhas-onibus" onChange={(e) => handleBusLineSelection(e.target.value)}>
                <option value="">Selecione uma linha de ônibus</option>
            </select>
            <input type="time" value={departureTime} onChange={(e) => handleDepartureTimeSelection(e.target.value)} />
            {selectedBusLine && (
                <div>
                    <h2>Ônibus da linha {selectedBusLine}</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Linha</th>
                                <th>Velocidade</th>
                                <th>Tempo até o local de partida</th>
                            </tr>
                        </thead>
                        <tbody>
                            {busData.map((bus, index) => (
                                <tr key={index}>
                                    <td>{bus.linha}</td>
                                    <td>{bus.velocidade} km/h</td>
                                    <td>{referenceTravelTime !== null ? `${referenceTravelTime} min` : '-'}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default App;
