import React, { useState, useEffect } from 'react';

const GeocodingSearch = ({ onPointSelect }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    // Sua lógica de pesquisa aqui, por exemplo, uma chamada para API de geocodificação
    // Substitua o seguinte trecho pela lógica real da sua API
    const fetchData = async () => {
      try {
        const response = await fetch(`https://api.example.com/geocode?search=${searchTerm}`);
        const data = await response.json();
        setResults(data.results);
      } catch (error) {
        console.error('Erro ao buscar resultados de geocodificação:', error);
      }
    };

    if (searchTerm !== '') {
      fetchData();
    } else {
      // Se o termo de pesquisa estiver vazio, limpe os resultados
      setResults([]);
    }
  }, [searchTerm]);

  const handlePointSelect = (result) => {
    setSearchTerm('');
    setResults([]); // Limpa os resultados ao selecionar um ponto
    onPointSelect(result);
  };

  return (
    <div>
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Digite sua busca..."
      />
      <ul>
        {Array.isArray(results) && results.length > 0 ? (
          results.map((result) => (
            <li key={result.id} onClick={() => handlePointSelect(result)}>
              {result.name}
            </li>
          ))
        ) : (
          <li>Sem resultados</li>
        )}
      </ul>
    </div>
  );
};

export default GeocodingSearch;
